glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform Constant {
    vec4 flameColor;
    vec2 centerUV;
    vec2 noiseScale;
    float edgeNoiseEffective;
    float flamePow;
    float flameIntensity;
    float radio;
    float st;
    float time;
};
void main(void) {
    vec3 finalColor = vec3(0.0);
    vec4 texColor = texture(texture, v_uv0);
    vec2 _st = vec2(st, 1.0);
    float dist = distance(v_uv0.xy * _st, centerUV * _st) * 2.0 + fbm(v_uv0 * _st * noiseScale + time) * edgeNoiseEffective - radio;
    float _step = 0.5;
    float textureColorVisibility = step(dist, _step);
    finalColor += textureColorVisibility * texColor.rgb;
    dist = 1.0 - abs(dist - _step);
    float flameVisibility = pow(dist, flamePow);
    finalColor += flameVisibility * flameColor.rgb * flameIntensity;
    float finalAlpha = textureColorVisibility + flameVisibility;
    gl_FragColor = vec4(finalColor, smoothstep(0.0, 0.7, finalAlpha)) * texColor.a;
}

