glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D bumpMap;
#endif
uniform Constant {
    vec4 flameColor;
    vec4 tillingOffset1;
    vec4 tillingOffset2;
    vec4 speed;
    float u_time;
    float normalInt;
    float st;
};
void main() {
    vec2 stUV = v_uv0 * vec2(st, 1.0);
    vec2 uv1 = stUV * tillingOffset1.xy + tillingOffset1.zw + u_time * speed.xy;
    uv1 = mod(uv1, 1.0);
    vec2 uv2 = stUV * tillingOffset2.xy + tillingOffset2.zw + u_time * speed.zw;
    uv2 = mod(uv2, 1.0);
    vec3 normalTS1 = texture2D(bumpMap, uv1).xyz;
    vec3 normalTS2 = texture2D(bumpMap, uv2).xyz;
    normalTS1 = normalTS1 * 2.0 - 1.0;
    normalTS2 = normalTS2 * 2.0 - 1.0;
    float intTS1 = dot(normalTS1.xy, vec2(1.0));
    float intTS2 = dot(normalTS2.xy, vec2(1.0));
    float totalInt = intTS1 + intTS2;
    vec3 normalTS = normalize(normalTS1 + normalTS2);
    vec3 offset = normalTS * -clamp(normalInt, 0.0, 1.0);
    vec2 fixedUV = v_uv0 + offset.xy;
    vec4 outColor = texture2D(texture, fixedUV);
    gl_FragColor = outColor * v_color;
}

