glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
  precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
  in vec4 v_color;
  #if USE_TEXTURE
  in vec2 v_uv0;
  uniform sampler2D texture;
  #endif
  uniform Constant {
    float u_time;
    float u_centerX;
    float u_centerY;
    float u_textureWidth;
    float u_textureHeight;
  };
  void main(void) {
    float radio = u_time / u_textureWidth;
    vec2 center = vec2(u_centerX / u_textureWidth, 1. - u_centerY / u_textureHeight);
    vec2 cord = vec2( abs((center.x - v_uv0.x) * (u_textureWidth / u_textureHeight)), abs(center.y - v_uv0.y) );
    float dis = sqrt((cord.x * cord.x + cord.y * cord.y));
    gl_FragColor = texture(texture, v_uv0) * (1.0 - dis / radio);
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
uniform mat4 cc_matWorld;
attribute vec3 a_position;
attribute vec4 a_color;
varying vec4 v_color;
#if USE_TEXTURE
attribute vec2 a_uv0;
varying vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl1.frag:
  precision highp float;
#if USE_ALPHA_TEST
#endif
  varying vec4 v_color;
  #if USE_TEXTURE
  varying vec2 v_uv0;
  uniform sampler2D texture;
  #endif
  uniform float u_time;
uniform float u_centerX;
uniform float u_centerY;
uniform float u_textureWidth;
uniform float u_textureHeight;
  void main(void) {
    float radio = u_time / u_textureWidth;
    vec2 center = vec2(u_centerX / u_textureWidth, 1. - u_centerY / u_textureHeight);
    vec2 cord = vec2( abs((center.x - v_uv0.x) * (u_textureWidth / u_textureHeight)), abs(center.y - v_uv0.y) );
    float dis = sqrt((cord.x * cord.x + cord.y * cord.y));
    gl_FragColor = texture2D(texture, v_uv0) * (1.0 - dis / radio);
}
