glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
#if USE_TEXTURE
  in vec2 v_uv0;
  uniform sampler2D texture;
#endif
uniform Constant {
  vec2 centerPos;
  vec2 featherStart;
  float featherOffset;
};
void main(void) {
  vec4 color = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    color *= texture(texture, v_uv0);
    float xx = pow((v_uv0.x - centerPos.x) / featherStart.x, 2.);
    float yy = pow((v_uv0.y - centerPos.y) / featherStart.y, 2.);
    float offset = pow(xx + yy, 0.5);
    color.a = pow(color.a, 1.0/3.);
    color.a *= smoothstep(featherOffset + featherStart.x, featherStart.x, featherStart.x * offset);
  #endif
  gl_FragColor = color * v_color;
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
uniform mat4 cc_matWorld;
attribute vec3 a_position;
attribute vec4 a_color;
varying vec4 v_color;
#if USE_TEXTURE
attribute vec2 a_uv0;
varying vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl1.frag:
precision highp float;
#if USE_ALPHA_TEST
#endif
varying vec4 v_color;
#if USE_TEXTURE
  varying vec2 v_uv0;
  uniform sampler2D texture;
#endif
uniform vec2 centerPos;
uniform vec2 featherStart;
uniform float featherOffset;
void main(void) {
  vec4 color = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    color *= texture2D(texture, v_uv0);
    float xx = pow((v_uv0.x - centerPos.x) / featherStart.x, 2.);
    float yy = pow((v_uv0.y - centerPos.y) / featherStart.y, 2.);
    float offset = pow(xx + yy, 0.5);
    color.a = pow(color.a, 1.0/3.);
    color.a *= smoothstep(featherOffset + featherStart.x, featherStart.x, featherStart.x * offset);
  #endif
  gl_FragColor = color * v_color;
}
