glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
in vec2 a_uv0;
out vec2 v_uv0;
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
  precision highp float;
  in vec4 v_color;
  in vec2 v_uv0;
  uniform sampler2D texture;
  uniform sampler2D textureY;
  uniform sampler2D textureU;
  uniform sampler2D textureV;
#if CRI_MANA_ALPHA
  uniform sampler2D textureA;
#endif
  uniform AdditionalProperties {
    float uScaleYA;
    float vScaleYA;
    float uScaleUV;
    float vScaleUV;
  };
  void main () {
    vec2 uvYA = v_uv0;
    uvYA.x *= uScaleYA / 2.0;
    uvYA.y *= vScaleYA / 2.0;
    vec2 uvUV = v_uv0;
    uvUV.x *= uScaleUV / 2.0;
    uvUV.y *= vScaleUV / 2.0;
    float color_y = texture2D(textureY, uvYA.xy).x;
    float color_u = texture2D(textureU, uvUV.xy).x - 128.0 / 255.0;
    float color_v = texture2D(textureV, uvUV.xy).x - 128.0 / 255.0;
    vec3 color = (vec3(color_y, color_y, color_y) - vec3(0.0625, 0.0625, 0.0625)) * 1.164;
    color.x += color_v * 1.596;
    color.y += -color_u * 0.392 - color_v * 0.813;
    color.z += color_u * 2.017;
    vec4 o = vec4(color.x, color.y, color.z, 1);
#if CRI_MANA_ALPHA
    o.w = texture2D(textureA, uvYA.xy).x;
#endif
    o *= v_color;
    gl_FragColor = o;
  }

