glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D baseColorSampler;
uniform sampler2D lightColorSampler;
uniform sampler2D bloomColorSampler;
#endif
  uniform Constant {
      float ambient;
  };
  void main(void) {
      vec3 finalColor = texture(lightColorSampler, v_uv0).rgb;
      finalColor.rgb *= texture(baseColorSampler, v_uv0).rgb;
      finalColor.rgb += texture(baseColorSampler, v_uv0).rgb * ambient;
      finalColor.rgb += texture(bloomColorSampler, v_uv0).rgb;
      gl_FragColor = vec4(finalColor, 1.0);
  }

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
uniform mat4 cc_matWorld;
attribute vec3 a_position;
attribute vec4 a_color;
varying vec4 v_color;
#if USE_TEXTURE
attribute vec2 a_uv0;
varying vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl1.frag:
precision highp float;
#if USE_ALPHA_TEST
#endif
varying vec4 v_color;
#if USE_TEXTURE
varying vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D baseColorSampler;
uniform sampler2D lightColorSampler;
uniform sampler2D bloomColorSampler;
#endif
  uniform float ambient;
  void main(void) {
      vec3 finalColor = texture2D(lightColorSampler, v_uv0).rgb;
      finalColor.rgb *= texture2D(baseColorSampler, v_uv0).rgb;
      finalColor.rgb += texture2D(baseColorSampler, v_uv0).rgb * ambient;
      finalColor.rgb += texture2D(bloomColorSampler, v_uv0).rgb;
      gl_FragColor = vec4(finalColor, 1.0);
  }
