glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
attribute vec3 a_position;
attribute vec2 a_uv0;
out vec2 uv0;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  uv0 = a_uv0;
}

glsl3.frag:
precision highp float;
#if USE_TEXTURE
in vec2 uv0;
uniform sampler2D texture;
#endif
uniform Constants {
  vec4 targetColor;
  float flipX;
  float flipY;
};
void main () {
  #if USE_TEXTURE
  vec2 coord = uv0.xy;
  vec4 color = texture2D(texture, coord);
  float opacity;
  float startOffset = flipX;
  float endOffset = 1.0 - flipY;
  if(coord.x < startOffset){
    opacity = 1.0 - (startOffset - coord.x) / startOffset;
  }else if(coord.x > endOffset){
    opacity = 1.0 - (uv0.x - endOffset) / flipY;
  }else{
    opacity = 1.0;
  }
  gl_FragColor = vec4(targetColor.r, targetColor.g, targetColor.b, color.a * opacity);
  #endif
}

